function confirm(e,s,t){bootbox.confirm({title:"Delete "+toTitleCase(e)+"?",message:"Do you really want to delete this "+e+"? This cannot be undone.",buttons:{cancel:{label:'<i class="fa fa-times"></i> No',className:"btn btn-danger btn-sm"},confirm:{label:'<i class="fa fa-check"></i> Yes',className:"btn btn-success btn-sm"}},callback:function(a){a&&$.ajax({type:"DELETE",url:"/"+e+"s/"+s,dataType:"JSON",success:function(){$(t).closest("tr").remove(),bootbox.alert({size:"small",title:"<i class='fa fa-check'></i> Success!",message:toTitleCase(e)+" deleted successfully!"}).find(".modal-header").css({"background-color":"#1AAF5D"})}})}})}function toTitleCase(e){return e.replace(/(?:^|\s)\w/g,function(e){return e.toUpperCase()})}